package com.aistudio.ecommerceconcept.androidimport PlatformConfigurationimport android.app.Applicationclass EcommerceConcernApp: Application() {    override fun onCreate() {        super.onCreate()        initPlatformSDK()    }}fun EcommerceConcernApp.initPlatformSDK() =    PlatformSDK.init(        configuration = PlatformConfiguration(androidContext = applicationContext))