package themeimport androidx.compose.runtime.Composableimport androidx.compose.runtime.CompositionLocalProvider@Composablefun EcommerceConceptTheme(content: @Composable () -> Unit) {    CompositionLocalProvider(        LocalColorProvider provides palette,        content = content    )}object Theme {    val colors: EcommerceConceptColors        @Composable        get() = LocalColorProvider.current}