package modelsdata class Products(	val bestSeller: List<Any>,	val homeStore: List<Any>,)data class HomeStoreItem(	val subtitle: String,	val id: Int,	val title: String,	val picture: String,	val isBuy: Boolean,	val isNew: Boolean,)data class BestSellerItem(	val isFavorites: Boolean,	val discountPrice: Int,	val id: Int,	val title: String,	val priceWithoutDiscount: Int,	val picture: String,)