import navigation.NavigationTreeimport ru.alexgladkov.odyssey.compose.extensions.flowimport ru.alexgladkov.odyssey.compose.extensions.screenimport ru.alexgladkov.odyssey.compose.navigation.RootComposeBuilderimport screens.BasketScreenimport screens.DetailsScreenimport screens.showcase.ShowcaseScreenfun RootComposeBuilder.shopFlow() {    flow(name = NavigationTree.Shop.Showcase.name) {        screen(name = NavigationTree.Shop.Showcase.name) {           ShowcaseScreen()        }        screen(name = NavigationTree.Shop.Details.name) {            DetailsScreen()        }        screen(name = NavigationTree.Shop.Basket.name){            BasketScreen()        }    }}