package networkDataSource.modelsimport kotlinx.serialization.SerialNameimport kotlinx.serialization.Serializableimport models.BestSellerItemimport models.HomeStoreItemimport models.Products@Serializabledata class ResponseShowcaseProducts(	@SerialName("best_seller")	val bestSeller: List<ResponseBestSellerItem>,	@SerialName("home_store")	val homeStore: List<ResponseHomeStoreItem>,)@Serializabledata class ResponseHomeStoreItem(	@SerialName("subtitle") val subtitle: String,	@SerialName("id") val id: Int,	@SerialName("title") val title: String,	@SerialName("picture") val picture: String,	@SerialName("is_buy") val isBuy: Boolean,	@SerialName("is_new") val isNew: Boolean,)@Serializabledata class ResponseBestSellerItem(	@SerialName("is_favorites") val isFavorites: Boolean,	@SerialName("discount_price") val discountPrice: Int,	@SerialName("id") val id: Int,	@SerialName("title") val title: String,	@SerialName("price_without_discount") val priceWithoutDiscount: Int,	@SerialName("picture") val picture: String,)fun ResponseShowcaseProducts.mapToProducts():Products =	Products(		homeStore = homeStore,		bestSeller = bestSeller,	)fun ResponseHomeStoreItem.mapToHomeStoreItem(): HomeStoreItem =    HomeStoreItem(        id = id,        title = title,        subtitle = subtitle,        isBuy = isBuy,        isNew = isNew,        picture = picture    )fun ResponseBestSellerItem.mapToBestSellerItem(): BestSellerItem =    BestSellerItem(        id = id,        isFavorites = isFavorites,        discountPrice = discountPrice,        title = title,        priceWithoutDiscount = priceWithoutDiscount,        picture = picture    )