import androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.isSystemInDarkThemeimport androidx.compose.runtime.Composableimport androidx.compose.runtime.CompositionLocalProviderimport androidx.compose.runtime.DisposableEffectimport com.adeo.kviewmodel.odyssey.setupWithViewModelsimport com.google.accompanist.systemuicontroller.rememberSystemUiControllerimport navigation.NavigationTreeimport navigation.generateGraphimport ru.alexgladkov.odyssey.compose.base.Navigatorimport ru.alexgladkov.odyssey.compose.extensions.setupWithActivityimport ru.alexgladkov.odyssey.compose.local.LocalRootControllerimport ru.alexgladkov.odyssey.compose.navigation.RootComposeBuilderimport ru.alexgladkov.odyssey.compose.navigation.modal_navigation.ModalNavigatorimport theme.EcommerceConceptThemeimport theme.Themefun ComponentActivity.setupThemedNavigation() {    val rootController = RootComposeBuilder().apply { generateGraph() }.build()    rootController.setupWithActivity(this)    rootController.setupWithViewModels()    setContent {        EcommerceConceptTheme {////            val backgroundColor = Theme.colors.secondaryBackground//            rootController.backgroundColor = backgroundColor            CompositionLocalProvider(                LocalRootController provides rootController            ) {                ModalNavigator {                    Navigator(startScreen = NavigationTree.Splash.SplashScreen.name)                }            }        }    }}