import androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport kotlinx.coroutines.delayimport navigation.NavigationTreeimport ru.alexgladkov.odyssey.compose.extensions.presentimport ru.alexgladkov.odyssey.compose.local.LocalRootControllerimport screen.components.Circleimport screen.components.TextSplashimport theme.Theme@Composablefun SplashScreen() {    val modifier = Modifier    val rootController = LocalRootController.current    Box(modifier = modifier        .background(color = Theme.colors.secondaryBackground)        .fillMaxSize(),        contentAlignment = Alignment.Center) {        Circle(modifier = modifier, color = Theme.colors.primaryAction)        TextSplash(modifier = modifier)    }    LaunchedEffect(key1 = Unit) {        delay(3000)        rootController.present(NavigationTree.Shop.Showcase.name)    }}