package screen.componentsimport androidx.compose.foundation.Canvasimport androidx.compose.foundation.layout.offsetimport androidx.compose.foundation.layout.sizeimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport theme.Theme@Composablefun Circle(modifier: Modifier, color: Color) {    Canvas(modifier = modifier.size(132.dp), onDraw = {        drawCircle(color = color)    })}@Composablefun TextSplash(modifier: Modifier) {    Text(text = "Ecommerce \n" +            "Concept ",        color = Theme.colors.secondaryTextColor,        lineHeight = 33.03.sp,        fontSize = 30.sp,        fontWeight = FontWeight(weight = 800),        modifier = modifier            .offset(x = 50.dp))}